import{_ as e,c as a,o as l,a as r}from"./app.6accdfa1.js";const b=JSON.parse('{"title":"Terminologie Constellation","description":"","frontmatter":{},"headers":[{"level":2,"title":"Client","slug":"client","link":"#client","children":[]},{"level":2,"title":"Profil","slug":"profil","link":"#profil","children":[]},{"level":2,"title":"Mots-clefs","slug":"mots-clefs","link":"#mots-clefs","children":[]},{"level":2,"title":"Bases de données","slug":"bases-de-donnees","link":"#bases-de-donnees","children":[]},{"level":2,"title":"Tableaux","slug":"tableaux","link":"#tableaux","children":[]},{"level":2,"title":"Variables","slug":"variables","link":"#variables","children":[]},{"level":2,"title":"Projet","slug":"projet","link":"#projet","children":[]},{"level":2,"title":"Réseau","slug":"reseau","link":"#reseau","children":[]},{"level":2,"title":"Recherche","slug":"recherche","link":"#recherche","children":[]},{"level":2,"title":"Nuée","slug":"nuee","link":"#nuee","children":[]}],"relativePath":"guide/concepts.md"}'),i={name:"guide/concepts.md"},s=r('<h1 id="terminologie-constellation" tabindex="-1">Terminologie Constellation <a class="header-anchor" href="#terminologie-constellation" aria-hidden="true">#</a></h1><p>Constellation ressemble beaucoup à un tableau LibreOffice ou Excel, mais avec quelques fonctionnalités en plus.</p><nav class="table-of-contents"><ul><li><a href="#client">Client</a></li><li><a href="#profil">Profil</a></li><li><a href="#mots-clefs">Mots-clefs</a></li><li><a href="#bases-de-donnees">Bases de données</a></li><li><a href="#tableaux">Tableaux</a></li><li><a href="#variables">Variables</a></li><li><a href="#projet">Projet</a></li><li><a href="#reseau">Réseau</a></li><li><a href="#recherche">Recherche</a></li><li><a href="#nuee">Nuée</a></li></ul></nav><h2 id="client" tabindex="-1">Client <a class="header-anchor" href="#client" aria-hidden="true">#</a></h2><p>Le <a href="/ipa/client.html">client</a> est le point central de Constellation. Chaque compte correspond à un client Constellation.</p><h2 id="profil" tabindex="-1">Profil <a class="header-anchor" href="#profil" aria-hidden="true">#</a></h2><p>Le <a href="/ipa/profil.html">profil</a> de Constellation organise les information du compte d&#39;utilisateur.</p><h2 id="mots-clefs" tabindex="-1">Mots-clefs <a class="header-anchor" href="#mots-clefs" aria-hidden="true">#</a></h2><p>Les <a href="/ipa/motsClefs.html">mots-clefs</a> servent à indexer les bases de données.</p><h2 id="bases-de-donnees" tabindex="-1">Bases de données <a class="header-anchor" href="#bases-de-donnees" aria-hidden="true">#</a></h2><p>L&#39;unité centrale de Constellation est la <a href="/ipa/bds.html">base de données</a>.</p><h2 id="tableaux" tabindex="-1">Tableaux <a class="header-anchor" href="#tableaux" aria-hidden="true">#</a></h2><p>Chaque base de données à un ou plusieurs <a href="/ipa/tableaux.html">tableaux</a>.</p><h2 id="variables" tabindex="-1">Variables <a class="header-anchor" href="#variables" aria-hidden="true">#</a></h2><p>Chaque colonne d&#39;un tableau est associée à une <a href="/ipa/variables.html">variable</a>.</p><h2 id="projet" tabindex="-1">Projet <a class="header-anchor" href="#projet" aria-hidden="true">#</a></h2><p>Un <a href="/ipa/projets.html">projet</a> regroupe plusieurs bases de données traitant du même sujet.</p><h2 id="reseau" tabindex="-1">Réseau <a class="header-anchor" href="#reseau" aria-hidden="true">#</a></h2><p>Le <a href="/ipa/réseau.html">réseau</a> vous permet d&#39;accéder aux informations des autres membres connectés.</p><h2 id="recherche" tabindex="-1">Recherche <a class="header-anchor" href="#recherche" aria-hidden="true">#</a></h2><p>Vous pouvez également <a href="/ipa/recherche.html">rechercher</a> les données qui vous intéressent.</p><h2 id="nuee" tabindex="-1">Nuée <a class="header-anchor" href="#nuee" aria-hidden="true">#</a></h2><p>Les <a href="/ipa/nuées.html">nuées</a> vous permettent de créer des projets de science citoyenne et de regrouper toutes les contributions de vos participants à la même place.</p>',23),t=[s];function n(o,h,d,c,u,p){return l(),a("div",null,t)}const m=e(i,[["render",n]]);export{b as __pageData,m as default};
