    oublierImage = await client.profil.suivreImage({ 
        idCompte: "id du compte qui m'int√©resse",
        f: x => image.value = x 
    });
})
onUnmounted(async () => {
    // √âviter une fuite de m√©moire lorsqu'on ferme la composante
    if (oublierImage) await oublierImage();
})

</script>

